// Kfir Tayar 208991430

const LocalStorage = {

    async getCostsList(month, year, key) {
        const allCosts = JSON.parse(localStorage.getItem(key));
        const filterdByYear = allCosts.filter(cost => year === parseInt(cost.date.slice(0,4)));
        const filterdByMonth = allCosts.filter(cost => month === parseInt(cost.date.slice(6,7)));
        const filterdByMonthAndYear = allCosts.filter(cost =>
            (month === parseInt(cost.date.slice(6,7))) && (year === parseInt(cost.date.slice(0,4))));
        return filterdByMonthAndYear;
    },

    async getTable(key){
      return JSON.parse(localStorage.getItem(key));
    },

    async addCost(key, value) {
        localStorage.setItem(key, JSON.stringify(value));
    },
};

export default LocalStorage;
