/* AboutPageStyles.js */
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
    container: {
        marginTop: theme.spacing(4),
    },
    paper: {
        padding: theme.spacing(3),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    profileImage: {
        width: '150px',
        height: '150px',
        borderRadius: '50%',
        objectFit: 'cover',
        marginBottom: theme.spacing(2),
    },
    title: {
        marginBottom: theme.spacing(2),
    },
    description: {
        textAlign: 'justify',
    },
}));

export default useStyles;
