import './contact.css';
import axios from 'axios';
import { useState } from "react";

import * as React from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Paper from "@mui/material/Paper";

function Copyright(props) {
    return (
        <Typography variant="body2" color="text.secondary" align="center" {...props}>
            {'Copyright © '}
            <Link color="inherit" href="#">
                Kfir Tayar
            </Link>{' '}
            {new Date().getFullYear()}
            {'.'}
        </Typography>
    );
}

const ContactPage = () => {

    // Creating variables with an initial state
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [company, setCompany] = useState('');
    const [mobile, setMobile] = useState('');
    const [email, setEmail] = useState('');
    const [jobTitle, setJobTitle] = useState('');
    const [description, setDescription] = useState('');

    // Clearing the fields after pressing the button "ADD COST"
    function clearButtons(){
        setFirstName('');
        setLastName('');
        setCompany('');
        setMobile('');
        setEmail('');
        setJobTitle('');
        setDescription('');
    }

    // const handleChange = (event) => {
    //     const { name, value } = event.target;
    //     switch (name) {
    //         case 'firstName':
    //             setFirstName(value);
    //             break;
    //         case 'lastName':
    //             setLastName(value);
    //             break;
    //         case 'company':
    //             setCompany(value);
    //             break;
    //         case 'mobile':
    //             setMobile(value);
    //             break;
    //         case 'email':
    //             setEmail(value);
    //             break;
    //         case 'jobTitle':
    //             setJobTitle(value);
    //             break;
    //         case 'description':
    //             setDescription(value);
    //             break;
    //         default:
    //             break;
    //     }
    // };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = {
            firstName,
            lastName,
            company,
            mobile,
            email,
            jobTitle,
            description,
        };

        try {
            const response = await axios.post('http://localhost:3001/api/contact', formData);
            alert('Data submitted successfully!', response.data);
        } catch (error) {
            alert('Error submitting data:', error);
        }

        clearButtons();
    }


    return (
        <Container className="contact-container" component="main" maxWidth="md">
            <Typography variant="h4" className="contact-options" align={"center"}>
                How to reach me?
            </Typography>

                <Paper className="contact-paper">
                    <p className='social-links'>
                        <a href="https://www.linkedin.com/in/kfir-tayar/" target="blank"><img src="https://cdn.jsdelivr.net/gh/devicons/devicon/icons/linkedin/linkedin-original.svg" alt="linkedin-page" height="60" width="60" /></a>
                    </p>
                    <h1>OR</h1>
                <Box component="form" noValidate onSubmit={handleSubmit} sx={{}}>
                    <h4>Fill the contact file and I'll be in touch :)</h4>
                    <Grid container spacing={2}>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                fullWidth
                                value={firstName}
                                id="firstName"
                                label="First Name"
                                autoComplete="given-name"
                                name="firstName"
                                onChange={e => setFirstName(e.target.value)}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                fullWidth
                                value={lastName}
                                id="lastName"
                                label="Last Name"
                                name="lastName"
                                onChange={e => setLastName(e.target.value)}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                fullWidth
                                value={company}
                                id="company"
                                label="Company"
                                name="company"
                                autoComplete="Null"
                                onChange={e => setCompany(e.target.value)}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                fullWidth
                                value={mobile}
                                id="mobile"
                                label="Mobile"
                                name="mobile"
                                onChange={e => setMobile(e.target.value)}
                            />
                        </Grid>
                        <Grid item sm={6}>
                            <TextField
                                required
                                fullWidth
                                value={email}
                                id="email"
                                label="Email Address"
                                name="email"
                                autoComplete="email"
                                onChange={e => setEmail(e.target.value)}
                            />
                        </Grid>
                        <Grid item sm={6}>
                            <TextField
                                required
                                fullWidth
                                value={jobTitle}
                                id="jobTitle"
                                label="Job Title"
                                name="job-title"
                                onChange={e => setJobTitle(e.target.value)}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                value={description}
                                name="decryption"
                                label="Tell me about this job offer"
                                type="text"
                                id="decryption"
                                multiline
                                rows={7}
                                autoComplete="Null"
                                onChange={e => setDescription(e.target.value)}
                            />
                        </Grid>
                    </Grid>
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        sx={{ mt: 3, mb: 2 }}
                    >
                        SEND
                    </Button>
                </Box>
                    <h1>OR</h1>
                    <a href="https://mail.google.com/mail/?view=cm&fs=1&to=kfirtayar145@gmail.com" target="_blank">
                        <img src="https://img.icons8.com/color/48/gmail-new.png" alt="gmail-new" height='60px' width='60px'/>
                    </a>

        </Paper>
            <Copyright sx={{ mt: 2 }} />
    </Container>
    );
}

export default ContactPage;
