// Kfir Tayar 208991430
//

import React from 'react';
import './addcost.css';
import {useState, useEffect} from 'react';
import LocalStorage from "./localStorage";


const Home = () => {
    // This useEffect hook updates the document title to 'Cost Manager'
    useEffect(() => {
        document.title = 'Cost Manager';
    }, []);

    useEffect(() => {
        // This useEffect hook retrieves the inputs saved in local storage and sets them to the inputs state variable
        const inputsFromLocalStorage = LocalStorage.get('inputs');
        if (inputsFromLocalStorage) {
            setInputs(inputsFromLocalStorage);
        }
    }, []);

    // These state variables are used to store the values of the inputs
    const [date, setDate] = useState('');
    const [item, setItem] = useState('');
    const [sum, setSum] = useState('');
    const [category, setCategory] = useState('food');
    const [currency, setCurrency] = useState('');
    const [description, setDescription] = useState('');
    const [inputs, setInputs] = useState([]);
    const [error, setError] = useState('');

    // This function is called when the user clicks the add cost button
    const today = new Date();
    const todayStr = today.toISOString().slice(0, 10);


    return (
        <div className="Home">
            <h1>Cost Manager APP<br/>
            Please insert your costs</h1>
            <div className="form-container">
                <div>
                    <div className="inputGroup">
                        <label htmlFor='name'>Date:</label>
                        <input type="date" value={date}
                               onChange={e => setDate(e.target.value)} required
                               autoComplete={'off'} max={todayStr}/>
                    </div>
                    <div className="inputGroup">
                        <label>Sum: </label><input type="number" value={sum}
                                                     onChange={e => setSum(e.target.value)}/>
                    </div>
                </div>

                <div>

                    <div className="inputGroup">
                        <label>Category:</label>
                        <select value={category} onChange={e => setCategory(e.target.value)}>
                            <option value="food">Food</option>
                            <option value="health">Health</option>
                            <option value="housing">Housing</option>
                            <option value="sport">Sport</option>
                            <option value="education">Education</option>
                            <option value="transportation">Transportation</option>
                            <option value="other">Other</option>
                        </select>
                    </div>

                    <div className="inputGroup">
                        <label>Currency:</label>
                        <select value={currency} onChange={e => setCurrency(e.target.value)}>
                            <option value="ILS">ILS</option>
                            <option value="USD">USD</option>
                            <option value="GPB">GPB</option>
                            <option value="EUR">EUR</option>
                            <option value="CNY">CNY</option>
                        </select>
                    </div>

                    <div className="inputGroup">
                        <label>Description: </label>
                        <textarea value={description} onChange={e => setDescription(e.target.value)}
                                  placeholder="Enter description"></textarea>
                    </div>

                </div>

                <div>

                        <div className="submit-button-add-cost">
                        </div>

                    <button className={"add-cost"} id="plus" onClick={() => {
                        if (!sum || !category || !description) {
                            alert("please enter all field")
                            return;
                        }
                        setError('');
                        setInputs([...inputs, {sum, currency, category, date, description}]);
                        localStorage.setItem('inputs', JSON.stringify([...inputs, {
                            sum,
                            currency,
                            category,
                            date,
                            description
                        }]));
                        setSum(''); // Clear the date input
                        setCurrency(''); // Clear the item input
                        setCategory(''); // Clear the price input
                        setDate('food'); // Reset the category to default
                        setDescription(''); // Clear the description input
                        alert("Cost added successfully");
                    }}>Add cost
                    </button>
                    {error && <p className="error">{error}</p>}
                </div>
            </div>



            {inputs.map((input, index) => (
            <table>
                <thead>
                <tr>
                    <th>Index</th>
                    <td>{input.index}</td>
                </tr>
                <tr>
                    <th>Sum</th>
                    <td>{input.sum}</td>
                </tr>
                <tr>
                    <th>Category</th>
                    <td>{input.category}</td>
                </tr>
                <tr>
                    <th>Currency</th>
                    <td>{input.currency}</td>
                </tr>
                <tr>
                    <th>Date</th>
                    <td>{input.date}</td>
                </tr>
                <tr>
                    <th>Description</th>
                    <td>{input.description}</td>
                </tr>
                </thead>
            </table>
            ))}
        </div>

    );
}
export default Home;
