import React from 'react';
import './addcost.css';
import {useState, useEffect} from 'react';
import LocalStorage from "./localStorage";

// Components from MUI lib
import Button from '@mui/material/Button';
import DeleteIcon from '@mui/icons-material/Delete';
import AddCard from '@mui/icons-material/AddCard';

//Table
import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import localStorage from "./localStorage";

const AddCostPage = () => {
    // This useEffect hook updates the document title to 'Cost Manager'
    useEffect(() => {
        document.title = 'Cost Manager App';
    }, []);

    useEffect(() => {
        // This useEffect hook retrieves the inputs saved in local storage and sets them to the inputs state variable
        const inputParameters = LocalStorage.get('costManagerList');
    }, []);

    // These state variables are used to store the values of the inputs
    const [date, setDate] = useState('');
    const [sum, setSum] = useState('');
    const [category, setCategory] = useState('');
    const [currency, setCurrency] = useState('');
    const [description, setDescription] = useState('');
    let [inputParameters, setInputParameters] = useState([]);

    function clearButtons(){
        setSum(''); // Clear the date input
        setCurrency(''); // Clear the item input
        setCategory(''); // Clear the price input
        setDate(''); // Reset the category to default
        setDescription(''); // Clear the description input
    }

    // This function is called when the user clicks the add cost button
    var index = 1;
    var key = 1;

    //Table style configuration
    const StyledTableCell = styled(TableCell)(({ theme }) => ({
        [`&.${tableCellClasses.head}`]: {
            backgroundColor: theme.palette.secondary,
            color: theme.palette.common.white,
        },
        [`&.${tableCellClasses.body}`]: {
            fontSize: 16,
        },
    }));
    const StyledTableRow = styled(TableRow)(({ theme }) => ({
        '&:nth-of-type(odd)': {
            backgroundColor: '#cfd8dc',
        },
    }));

    return (
        <div className="add-cost-page">
            <h1>Cost Manager App<br/>
                Please insert your costs</h1>
            <div className="add-cost-form">

                <div>
                    <div className="add-cost-form-fields">
                        <label className='add-cost-form-labels'>Date:</label>
                        <input type="date" value={date}
                               onChange={e => setDate(e.target.value)} required
                               autoComplete={'off'} max={''}/>
                    </div>
                    <div className="add-cost-form-fields">
                        <label className='add-cost-form-labels'>Sum:</label><input type="number" value={sum}
                                                     onChange={e => setSum(e.target.value)}/>
                    </div>
                </div>

                <div>
                    <div className="add-cost-form-fields">
                        <label className='add-cost-form-labels'>Category:</label>
                        <select value={category} onChange={e => setCategory(e.target.value)}>
                            <option value="food">Food</option>
                            <option value="health">Health</option>
                            <option value="housing">Housing</option>
                            <option value="sport">Sport</option>
                            <option value="education">Education</option>
                            <option value="transportation">Transportation</option>
                            <option value="other">Other</option>
                        </select>
                    </div>

                    <div className="add-cost-form-fields">
                        <label className='add-cost-form-labels'>Currency:</label>
                        <select value={currency} onChange={e => setCurrency(e.target.value)}>
                            <option value="ILS">ILS</option>
                            <option value="USD">USD</option>
                            <option value="GPB">GPB</option>
                            <option value="EUR">EUR</option>
                            <option value="CNY">CNY</option>
                        </select>
                    </div>
                </div>

                <div>
                    <div className="add-cost-form-fields">
                        <label className='add-cost-form-labels'>Description: </label>
                        <textarea value={description} onChange={e => setDescription(e.target.value)}></textarea>
                    </div>


                    <Button fullWidth='True' variant="contained" color="secondary" endIcon={<AddCard />}
                            onClick={() => {

                        // Validate if the necessary categories are exist
                        if (!sum || !category || !description) {
                            alert("One or more of necessary fields are not passed!")
                            return;
                        }

                        // Passing the inputs into an array
                        const cost = {date, sum, category, currency, description};

                        //Using localStorage to save the input parameters
                        localStorage.set(1, cost);

                        setInputParameters = localStorage.get("costManagerList");

                        //Clear the contact of buttons
                        clearButtons();

                        alert("New cost has been created!");
                    }}>Add cost
                    </Button>
                </div>
            </div>

            <div className='add-cost-form-table'>
                <TableContainer component={Paper}>
                    <Table size={'small'}  sx={{ minWidth: 700 }} aria-label="customized table">
                        <TableHead className='add-cost-form-header'>
                            <TableRow>
                                <StyledTableCell>Index</StyledTableCell>
                                <StyledTableCell align="right">Sum</StyledTableCell>
                                <StyledTableCell align="right">Category</StyledTableCell>
                                <StyledTableCell align="right">Currency</StyledTableCell>
                                <StyledTableCell align="right">Date</StyledTableCell>
                                <StyledTableCell align="right">Description</StyledTableCell>
                                <StyledTableCell align="right">Delete Cost</StyledTableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {inputParameters.map((input) => (
                                <StyledTableRow key={input.index}>
                                    <StyledTableCell component="th" scope="row">
                                        {index++}
                                    </StyledTableCell>
                                    <StyledTableCell align="right">{input.sum}</StyledTableCell>
                                    <StyledTableCell align="right">{input.category}</StyledTableCell>
                                    <StyledTableCell align="right">{input.currency}</StyledTableCell>
                                    <StyledTableCell align="right">{input.date}</StyledTableCell>
                                    <StyledTableCell align="right">{input.description}</StyledTableCell>
                                    <Button
                                        variant="outlined" startIcon={<DeleteIcon />} size="medium" color="error" onClick={() => {
                                            LocalStorage.remove('costManagerList')
                                    }}
                                    >
                                        Delete cost
                                    </Button>
                                </StyledTableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </div>
        </div>
    );
}
export default AddCostPage;
